import React from 'react' ;
import './DescriptionBox.css'

 const DescriptionBox = () => {
    return(
        <div className='descriptionbox'> 
        <div className='descriptionbox-navigator'>
            <div className='descriptionbox-nav-box'>Description </div>
            <div className='descriptionbox-nav-boxx'>Reviews (122)</div>
        </div>
        <div className='descriptionbox-description'>
        <p>An e-commerce platform specializing in the sale of clothing online provides an immersive and convenient shopping experience tailored to the needs of modern consumers. Boasting a user-friendly interface, the website offers an extensive array of clothing items spanning across various categories, including men's wear, women's wear, kids' clothing, and accessories. Each product listing is accompanied by detailed descriptions, high-resolution images, size guides, and customer reviews, empowering shoppers to make informed purchasing decisions. With personalized recommendations based on browsing history and preferences, users can discover new styles and trends that resonate with their individual tastes.</p>  

         <p>An e-commerce website dedicated to selling clothes online offers a convenient and diverse platform for users to browse, select, and purchase a wide range of clothing items from the comfort of their own homes. </p> 
        </div>
        </div>
    )
 }

 export default  DescriptionBox;
